mapper.select:
select no , userid, username, clubno, clubid, invite, submit, complete, date_format(createdate,'%Y-%m-%d') createdate
from clubinvite
where userid=#{param1} and clubno=${param2}

mapper.update:
update clubinvite 
set complete = true
where no=${param1}

update club
set clubid = #{clubid} , clubnotice=#{clubnotice}
where no=${no}
